import React from 'react'
import {View, StyleSheet, Image} from 'react-native'
import Man from "./assets/man.svg"
import Woman from "./assets/woman.svg"
import  { ReactComponent as Shirt } from "./assets/t-shirt.svg"
import  { ReactComponent as Body } from "./assets/skin.svg"

class MainComponent extends React.Component{

    render() {
        return (
            <View style={{flex:1,}}>
                <View style={styles.temp_style}>
                    <View style={styles.profilTxt}>
                        <View style={{marginTop:10,marginBottom:10}}>
                            PROFIL
                        </View>
                    </View>
                    <View style={styles.body_profil}>
                        <View style={{marginTop:20,marginBottom:20}}>
                            Lequel de ces deux avatars préférez-vous ?
                        </View>
                        <View style={styles.face_body}>
                            <Image
                                source={Man}
                                style={styles.face_svg}
                            />
                            <Image
                                source={Woman}
                                style={styles.face_svg}
                            />
                            <Shirt
                            />
                            <Body
                            />
                        </View>
                    </View>
                </View>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    temp_style:{
        margin: "20%",
        // display: 'flex',
        // justifyContent: 'center',
        // alignItems: 'center',
    },
    profilTxt:{
        width:"100%",
        alignItems: 'center',
        backgroundColor: "purple",
        color:"white",
        fontWeight:"bold",
        flex:1
    },
    body_profil:{
        width:"100%",
        alignItems: 'center',
        flex:10,
        fontWeight:"lighter",
        borderColor:'purple',
        borderWidth:2,
    },
    face_body:{

    },
    face_svg:{
        width:"100%",
        height:"100%"
    }
})

export default MainComponent
